#!/bin/bashset -e# Caminho onde o ZenML vai manter o repositÃ³rioZENML_REPO_PATH="/app/.zen"# Inicializa o ZenML se ainda nÃ£o houver configuraÃ§Ã£oif [ ! -d "$ZENML_REPO_PATH" ]; then    echo "Initializing ZenML repository..."    zenml initelse    echo "ZenML repository already initialized at $ZENML_REPO_PATH."fi# Garante que o servidor MLflow esteja registrado no ZenMLif ! zenml experiment-tracker list | grep -q "mlflow_tracker"; then    echo "Registering MLflow experiment tracker..."    zenml experiment-tracker register mlflow_tracker \        --flavor=mlflow \        --tracking_uri=http://mlflow_app:5000else    echo "MLflow experiment tracker already registered."fi# Define o tracker ativo (idempotente)zenml experiment-tracker set mlflow_tracker# Sobe o ZenML Dashboard (de forma bloqueante)echo "Starting ZenML Dashboard..."zenml up --blocking
